/* global describe, it, beforeEach */
"use strict";

function _asyncToGenerator(fn) { return function () { var gen = fn.apply(this, arguments); return new Promise(function (resolve, reject) { function step(key, arg) { try { var info = gen[key](arg); var value = info.value; } catch (error) { reject(error); return; } if (info.done) { resolve(value); } else { return Promise.resolve(value).then(function (value) { step("next", value); }, function (err) { step("throw", err); }); } } return step("next"); }); }; }

const path = require("path");
const foxxUtil = require("./util");
const expect = require("chai").expect;
const os = require("os");
const fs = require("fs");
const rmDir = require("./fs").rmDir;
const tmpDir = path.resolve(os.tmpdir(), "test-init-service");
const foxx = command => foxxUtil(command, false, { cwd: tmpDir });

const checkFile = (file, content) => {
  const filePath = path.resolve(tmpDir, file);
  expect(fs.existsSync(filePath)).to.equal(true);
  expect(fs.readFileSync(filePath, "utf-8").replace(/\r/g, "")).to.equal(content);
};

describe("Foxx service add test", () => {
  beforeEach(_asyncToGenerator(function* () {
    if (fs.existsSync(tmpDir)) {
      try {
        rmDir(tmpDir);
      } catch (e) {
        // noop
      }
    }
    yield foxxUtil(`init ${tmpDir}`);
  }));

  it("should create the test Javascript file", _asyncToGenerator(function* () {
    yield foxx(`add test hello`);
    checkFile("test/hello.js", "/*global describe, it, before, after, beforeEach, afterEach */\n'use strict';\nconst expect = require('chai').expect;\n\ndescribe('test suite', () => {\n  it('contains a test case', () => {\n    expect(true).not.to.equal(false);\n  });\n});\n");
  }));

  describe("with missing property tests in manifest.json", () => {
    const manifestPath = path.resolve(tmpDir, "manifest.json");

    beforeEach(_asyncToGenerator(function* () {
      const manifest = JSON.parse(fs.readFileSync(manifestPath, "utf-8"));
      manifest.tests = undefined;
      fs.writeFileSync(manifestPath, JSON.stringify(manifest, null, 2));
    }));

    it("should add tests pattern to manifest.json", _asyncToGenerator(function* () {
      let manifest = JSON.parse(fs.readFileSync(manifestPath, "utf-8"));
      expect(manifest).to.not.have.property("tests");
      yield foxx(`add test hello`);
      manifest = JSON.parse(fs.readFileSync(manifestPath, "utf-8"));
      expect(manifest).to.have.property("tests", "test/**/*.js");
    }));
  });
});