/* global describe, it, before */
"use strict";

function _asyncToGenerator(fn) { return function () { var gen = fn.apply(this, arguments); return new Promise(function (resolve, reject) { function step(key, arg) { try { var info = gen[key](arg); var value = info.value; } catch (error) { reject(error); return; } if (info.done) { resolve(value); } else { return Promise.resolve(value).then(function (value) { step("next", value); }, function (err) { step("throw", err); }); } } return step("next"); }); }; }

const path = require("path");
const foxx = require("./util");
const expect = require("chai").expect;
const os = require("os");
const fs = require("fs");

const foxxRcFile = path.resolve(os.tmpdir(), ".foxxrc");

describe("Foxx server list", () => {
  before(_asyncToGenerator(function* () {
    if (fs.existsSync(foxxRcFile)) {
      fs.unlinkSync(foxxRcFile);
    }
    yield foxx("server set test1 //localhost:8529");
    yield foxx("server set test2 //localhost:8530");
  }));

  it("should include added server", _asyncToGenerator(function* () {
    const server = yield foxx("server list");
    expect(server).to.equal("test1\ntest2\n");
  }));

  it("via alias should include added server", _asyncToGenerator(function* () {
    const server = yield foxx("remote ls");
    expect(server).to.equal("test1\ntest2\n");
  }));

  it("verbose should include added server with URLs", _asyncToGenerator(function* () {
    const server = yield foxx("server list --verbose");
    expect(server).to.equal("  test1  http://localhost:8529\n  test2  http://localhost:8530\n");
  }));

  it("verbose via alias should include added server with URLs", _asyncToGenerator(function* () {
    const server = yield foxx("server list -v");
    expect(server).to.equal("  test1  http://localhost:8529\n  test2  http://localhost:8530\n");
  }));
});