/* global describe, it, before, after, beforeEach, afterEach */
"use strict";

function _asyncToGenerator(fn) { return function () { var gen = fn.apply(this, arguments); return new Promise(function (resolve, reject) { function step(key, arg) { try { var info = gen[key](arg); var value = info.value; } catch (error) { reject(error); return; } if (info.done) { resolve(value); } else { return Promise.resolve(value).then(function (value) { step("next", value); }, function (err) { step("throw", err); }); } } return step("next"); }); }; }

const path = require("path");
const Database = require("arangojs");
const expect = require("chai").expect;
const foxx = require("./util");
const fs = require("fs");

const ARANGO_VERSION = Number(process.env.ARANGO_VERSION || 30000);
const ARANGO_URL = process.env.TEST_ARANGODB_URL || "http://localhost:8529";
const ARANGO_USERNAME = process.env.ARANGO_USERNAME || "root";

const mount = "/dev-test";
const basePath = path.resolve(__dirname, "..", "..", "fixtures");

describe("Foxx service development mode", () => {
  const db = new Database({
    url: ARANGO_URL,
    arangoVersion: ARANGO_VERSION
  });

  beforeEach(_asyncToGenerator(function* () {
    yield db.installService(mount, fs.readFileSync(path.resolve(basePath, "minimal-working-service.zip")));
  }));

  afterEach(_asyncToGenerator(function* () {
    try {
      yield db.uninstallService(mount, { force: true });
    } catch (e) {
      // noop
    }
  }));

  it("should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("should be activated via alias", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-development ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative server URL should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev --server ${ARANGO_URL} ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative server URL (short option) should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev -H ${ARANGO_URL} ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative database should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev --database _system ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative database (short option) should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev -D _system ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative username should be activated", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev --username ${ARANGO_USERNAME} ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  it("with alternative username should be activated (short option)", _asyncToGenerator(function* () {
    const infoBefore = yield db.getService(mount);
    expect(infoBefore.development).to.equal(false);
    yield foxx(`set-dev -u ${ARANGO_USERNAME} ${mount}`);
    const infoAfter = yield db.getService(mount);
    expect(infoAfter.development).to.equal(true);
  }));

  describe("with a password file", () => {
    const user = "testuser";
    const passwordFilePath = path.resolve(basePath, "passwordFile");
    const passwd = fs.readFileSync(passwordFilePath, "utf-8");
    before(_asyncToGenerator(function* () {
      db.route("/_api/user").post({
        user,
        passwd
      });
      db.route(`/_api/user/${user}/database/_system`).put({ grant: "rw" });
    }));
    after(_asyncToGenerator(function* () {
      try {
        db.route(`/_api/user/${user}`).delete();
      } catch (e) {
        // noop
      }
    }));
    it("should be activated", _asyncToGenerator(function* () {
      const infoBefore = yield db.getService(mount);
      expect(infoBefore.development).to.equal(false);
      yield foxx(`set-dev --username ${user} --password-file ${passwordFilePath} ${mount}`);
      const infoAfter = yield db.getService(mount);
      expect(infoAfter.development).to.equal(true);
    }));
  });

  it("should fail when mount is invalid", _asyncToGenerator(function* () {
    try {
      yield foxx("set-dev /dev/null");
    } catch (e) {
      return;
    }
    expect.fail();
  }));
});