/* global describe, it, before, after */
"use strict";

function _asyncToGenerator(fn) { return function () { var gen = fn.apply(this, arguments); return new Promise(function (resolve, reject) { function step(key, arg) { try { var info = gen[key](arg); var value = info.value; } catch (error) { reject(error); return; } if (info.done) { resolve(value); } else { return Promise.resolve(value).then(function (value) { step("next", value); }, function (err) { step("throw", err); }); } } return step("next"); }); }; }

const path = require("path");
const Database = require("arangojs");
const foxx = require("./util");
const expect = require("chai").expect;
const fs = require("fs");

const ARANGO_VERSION = Number(process.env.ARANGO_VERSION || 30000);
const ARANGO_URL = process.env.TEST_ARANGODB_URL || "http://localhost:8529";
const ARANGO_USERNAME = process.env.ARANGO_USERNAME || "root";

const mount = "/test-test";
const basePath = path.resolve(__dirname, "..", "..", "fixtures");

describe("Foxx service test", () => {
  const db = new Database({
    url: ARANGO_URL,
    arangoVersion: ARANGO_VERSION
  });

  before(_asyncToGenerator(function* () {
    yield db.installService(mount, fs.readFileSync(path.resolve(basePath, "with-tests.zip")));
  }));

  after(_asyncToGenerator(function* () {
    try {
      yield db.uninstallService(mount, { force: true });
    } catch (e) {
      // noop
    }
  }));

  it("should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount}`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative server URL should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} --server ${ARANGO_URL}`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative server URL (short option) should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} -H ${ARANGO_URL}`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative database should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} --database _system`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative database (short option) should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} -D _system`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative username should print test result", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} --username ${ARANGO_USERNAME}`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  it("with alternative username should print test result (short option)", _asyncToGenerator(function* () {
    try {
      yield foxx(`test ${mount} -u ${ARANGO_USERNAME}`);
    } catch (e) {
      const result = e.stdout.toString("utf-8");
      expect(result).to.has.string("4 passing");
      expect(result).to.has.string("2 failing");
    }
  }));

  describe("with a password file", () => {
    const user = "testuser";
    const passwordFilePath = path.resolve(basePath, "passwordFile");
    const passwd = fs.readFileSync(passwordFilePath, "utf-8");
    before(_asyncToGenerator(function* () {
      db.route("/_api/user").post({
        user,
        passwd
      });
      db.route(`/_api/user/${user}/database/_system`).put({ grant: "rw" });
    }));
    after(_asyncToGenerator(function* () {
      try {
        db.route(`/_api/user/${user}`).delete();
      } catch (e) {
        // noop
      }
    }));
    it("should print test result", _asyncToGenerator(function* () {
      try {
        yield foxx(`test ${mount} --username ${user} --password-file ${passwordFilePath}`);
      } catch (e) {
        const result = e.stdout.toString("utf-8");
        expect(result).to.has.string("4 passing");
        expect(result).to.has.string("2 failing");
      }
    }));
  });

  it("should fail when mount is invalid", _asyncToGenerator(function* () {
    try {
      yield foxx(`test /dev/null echo`);
    } catch (e) {
      return;
    }
    expect.fail();
  }));
});